import React, { useState, useEffect} from 'react';

const PostCustomer = ()=>{
    const [AllCustomers,setCustomers] = useState([]);
    const GetCustomers = async()=> {
      try {
        const get_cust = await fetch('http://localhost:5000/customer');
        const data =await get_cust.json();
        setCustomers(data);
        console.log(data);
    } catch (error) {
        console.log(error);
    }
    };

    useEffect(()=>{
      GetCustomers();
  },[]);

    return (
        <div>
        <div className='container'>
        <form className='jumbotron'>
        <h1>Add Customer</h1>
            <hr></hr>
  <div className="form-group">
    <label>Name</label>
    <input type="text" className="form-control" aria-describedby="emailHelp"  required/>
    <label >Phone</label>
    <input type="text" className="form-control"  />
    <label>Email</label>
    <input type="email" className="form-control" aria-describedby="emailHelp"  required/>
    <label >House No.</label>
    <input type="text" className="form-control"  />
    <label>City</label>
    <input type="text" className="form-control" aria-describedby="emailHelp"  required/>
    <label >Zip Code</label>
    <input type="text" className="form-control"  />
    <label>Username</label>
    <input type="text" className="form-control" aria-describedby="emailHelp"  required/>
    <label >Password</label>
    <input type="text" className="form-control"  />
  </div>

  <button type="submit" className="btn btn-primary">Add</button>
</form>
</div>
<div className='p-3'>
<h1 style={{textAlign : 'center'}}>All Customers</h1>
<table className="table">
  <thead className="thead-dark">
    <tr>
      <th scope="col">#</th>
      <th scope="col">Name</th>
      <th scope="col">Phone</th>
      <th scope="col">Email</th>
      <th scope="col">House No</th>
      <th scope="col">City</th>
      <th scope="col">Zip Code</th>
      <th scope="col">~</th>
    </tr>
  </thead>
  <tbody>
  {AllCustomers.map(customer => (
          <tr key={customer.customer_id}>
              <td>{customer.customer_id}</td>
              <td>{customer.name}</td>
              <td>{customer.phone}</td>
              <td>{customer.email}</td>
              <td>{customer.house_no}</td>
              <td>{customer.city}</td>
              <td></td>

          </tr>
      ))}
  </tbody>
</table>
</div>
</div>
    );
};

export default PostCustomer;
