import React,{useState} from 'react';
import customer from '../images/customer.png';
const CustomerLogin = ()=>{

    return (
<div>
    <br></br>
<h1 className="mt-5 mb-5" style={{textAlign : "center"}}>Customer Login</h1>
<div className="border container p-0 shadow-lg p-3 mb-5 bg-white rounded "  style={{display: 'flex',  justifyContent:'center'}}>
    <img src={customer} alt="customer missing" className="rounded mx-auto d-block ml-10 mr-10 flex-left p-5" height="400px"></img>
    <form className='container flex-right p-5 ' style={{alignSelf : "right"}} action='http://localhost:3000/customer' onSubmit={Login}>

  <div className="form-group">
    <label className="mt-4">Username</label>
    <input type="text" className="form-control mb-3" id="exampleInputEmail1" name="username" aria-describedby="emailHelp" onChange={e=>setUserName(e.target.value)} required/>
    <label >Password</label>
    <input type="text" className="form-control" id="exampleInputPassword1" onChange={e=>setPassword(e.target.value)}  required/>
  </div>

  <button type="submit" class="btn btn-primary btn-lg mt-3">Login</button>
</form>
</div>
</div>
    );
};

export default CustomerLogin;
